const ADD_POST = 'ADD_POST';
const ADD_LIKE = 'ADD_LIKE';
const CHANGE_NEW_POST_TEXT = 'CHANGE_NEW_POST_TEXT';


let store = {

_notifySubscriber() {
    console.log('no subscriber')
},

subscribe(subscriber) {
    this._notifySubscriber = subscriber;
},

_state: {
    posts: [
        {id:0, text:"Hello, my dear friend!", likes: 5},
        {id:1, text:"Hello, How are You?", likes: 10},
        {id:2, text:"Wow", likes: 0}
    ],
    newPostText: ""
},

getState() {
    return this._state;
},

dispatch(action) {   
    switch(action.type) {
        case ADD_POST: 
            let post = {
                id: this._state.posts.length,
                text: this._state.newPostText,
                likes: 0
            }
            this._state.posts.push(post);
            break;
        
        case ADD_LIKE: 
            this._state.posts[action.id].likes++;
            break

        case CHANGE_NEW_POST_TEXT: 
            this._state.newPostText = action.text;
            break;

        default: 
            break;
    }
    this._notifySubscriber();
}
}

export default store;


export const addPostActionCreator = () => {
    return { type: ADD_POST}
}
