let notifySubscriber = () => {
    console.log('no subscriber')
};

export const subscribe = (subscriber) => {
    notifySubscriber = subscriber;
}

const state = {
    posts: [
        {id:0, text:"Hello, my dear friend!", likes: 5},
        {id:1, text:"Hello, How are You?", likes: 10},
        {id:2, text:"Wow", likes: 0}
    ],
    newPostText: ""
}

export const addPost = () => {

    notifySubscriber();
}

export const addLike = (id) => {
    state.posts[id].likes++;
    notifySubscriber();
}

export const changeNewPostText = (text) => {
    state.newPostText = text;
    notifySubscriber();
}

export const dispatch = (action) => {   
    switch(action.type) {
        case 'ADD_POST': 
            let post = {
                id: state.posts.length,
                text: state.newPostText,
                likes: 0
            }
            state.posts.push(post);
            break;
        
        case 'ADD_LIKE': state.posts[id].likes++;
            

        default: break;
    }
}

export default state;
